import React, { useContext } from 'react';
import { FavoritesContext } from '../FavoritesContext';

const MovieCard = ({ movie }) => {
    const { favorites, addFavorite, removeFavorite } = useContext(FavoritesContext);
    const isFavorite = favorites.some(fav => fav.id === movie.id);

    const handleFavoriteClick = (e) => {
        e.stopPropagation();
        if (isFavorite) {
            removeFavorite(movie.id);
        } else {
            addFavorite(movie);
        }
    };

    return (
        <div
            style={{
                border: '1px solid #ddd',
                borderRadius: '10px',
                padding: '15px',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                backgroundColor: '#fff',
                transition: 'transform 0.3s, box-shadow 0.3s',
                cursor: 'pointer',
                textAlign: 'center',
                marginBottom: '20px',
                maxWidth: '250px',
            }}
            onClick={() => {/* Handle enlarging the movie */}}
            onMouseOver={(e) => {
                e.currentTarget.style.transform = 'scale(1.05)';
                e.currentTarget.style.boxShadow = '0 8px 16px rgba(0, 0, 0, 0.2)';
            }}
            onMouseOut={(e) => {
                e.currentTarget.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = '0 4px 8px rgba(0, 0, 0, 0.1)';
            }}
        >
            <img
                src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                alt={movie.title}
                style={{
                    width: '100%',
                    height: 'auto',
                    borderRadius: '8px',
                    marginBottom: '10px',
                    transition: 'opacity 0.3s',
                }}
                onMouseOver={(e) => e.currentTarget.style.opacity = '0.9'}
                onMouseOut={(e) => e.currentTarget.style.opacity = '1'}
            />
            <h3
                style={{
                    fontSize: '1.2em',
                    color: '#333',
                    margin: '10px 0',
                }}
            >
                {movie.title}
            </h3>
            <p
                className="movie-description"
                style={{
                    fontSize: '0.9em',
                    color: '#777',
                    marginBottom: '15px',
                    height: '60px',
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                }}
            >
                {movie.overview || 'No description available.'}
            </p>

            <p
                className="movie-rating"
                style={{
                    fontSize: '0.9em',
                    fontWeight: 'bold',
                    color: '#007bff',
                    marginBottom: '15px',
                }}
            >
                <strong>Rating:</strong> {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'}/10
            </p>

            <button
                onClick={handleFavoriteClick}
                style={{
                    padding: '10px 15px',
                    border: 'none',
                    borderRadius: '8px',
                    backgroundColor: isFavorite ? '#ff4b5c' : '#007bff',
                    color: '#fff',
                    cursor: 'pointer',
                    transition: 'background-color 0.3s',
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = isFavorite ? '#d43f4a' : '#0056b3'}
                onMouseOut={(e) => e.target.style.backgroundColor = isFavorite ? '#ff4b5c' : '#007bff'}
            >
                {isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
            </button>
        </div>
    );
};

export default MovieCard;
