import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext';
import Login from './components/Login';
import SignUp from './components/Signup';
import NavBar from './components/NavBar'; // Import NavBar

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [selectedLanguage, setSelectedLanguage] = useState('ta');
    const [searchQuery, setSearchQuery] = useState('');
    const [favorites, setFavorites] = useState([]);
    const [showFavorites, setShowFavorites] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isSignUp, setIsSignUp] = useState(false);
    const [isUserSignedUp, setIsUserSignedUp] = useState(false);

    // Fetch movies (rest of your movie fetching logic remains the same)

    // Toggle favorite
    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id));
        } else {
            setFavorites([...favorites, movie]);
        }
    };

    const clearSearch = () => {
        setSearchQuery('');
        setMovies([]);
    };

    const handleLogout = () => {
        localStorage.removeItem('userId');
        localStorage.removeItem('favorites');
        localStorage.removeItem('token');
        setIsLoggedIn(false); // Update the isLoggedIn state
    };

    return (
        <FavoritesProvider>
            <div style={{
                fontFamily: '"Poppins", sans-serif',
                backgroundColor: '#fff',
                color: '#333',
                minHeight: '100vh',
                padding: '40px',
                maxWidth: '1200px',
                margin: '0 auto',
                borderRadius: '10px',
            }}>
                {isLoggedIn ? (
                    <>
                        <NavBar handleLogout={handleLogout} /> {/* Use NavBar with the logout handler */}
                        <h1 style={{
                            textAlign: 'center',
                            color: '#ff6b00',
                            fontSize: '2.5rem',
                            marginBottom: '30px',
                            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.2)',
                        }}>
                            Movie Recommendations
                        </h1>
                        {/* Rest of the logged-in view */}
                        <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
                            <button onClick={() => setShowFavorites(false)} style={{
                                backgroundColor: showFavorites ? '#ddd' : '#ff6b00',
                                color: '#fff',
                                padding: '10px 15px',
                                borderRadius: '8px',
                                marginRight: '10px',
                                cursor: 'pointer',
                                border: 'none',
                                transition: 'background-color 0.3s',
                            }}>
                                Home
                            </button>
                            <button onClick={() => setShowFavorites(true)} style={{
                                backgroundColor: showFavorites ? '#ff6b00' : '#ddd',
                                color: '#fff',
                                padding: '10px 15px',
                                borderRadius: '8px',
                                cursor: 'pointer',
                                border: 'none',
                                transition: 'background-color 0.3s',
                            }}>
                                Favorites
                            </button>
                        </div>
                        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                            <label htmlFor="language-select" style={{ marginRight: '10px' }}>Select Language:</label>
                            <select id="language-select" value={selectedLanguage} onChange={(e) => setSelectedLanguage(e.target.value)} style={{
                                padding: '10px',
                                borderRadius: '5px',
                                border: '1px solid #ccc',
                            }}>
                                {/* Render selected languages */}
                            </select>
                        </div>
                        {!showFavorites ? (
                            <>
                                {/* Home and Search components */}
                            </>
                        ) : (
                            <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                        )}
                    </>
                ) : (
                    !isSignUp ? (
                        <Login onLogin={() => setIsLoggedIn(true)} onToggleSignUp={() => setIsSignUp(true)} />
                    ) : (
                        <SignUp onSignUp={() => {
                            setIsUserSignedUp(true);
                            setIsLoggedIn(true);
                        }} />
                    )
                )}
            </div>
        </FavoritesProvider>
    );
};

export default App;
