import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SearchBar from './components/SearchBar';
import FilterBar from './components/FilterBar';
import MovieList from './components/MovieList';

const TMDB_API_KEY = 'your_api_key_here';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch genres (Comedy, Crime, Horror)
        const fetchGenres = async () => {
            try {
                const response = await axios.get(
                    `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=en-US`
                );
                const allowedGenres = response.data.genres.filter(genre =>
                    ['Comedy', 'Crime', 'Horror'].includes(genre.name)
                );
                setGenres(allowedGenres);
            } catch (error) {
                setError('Error fetching genres.');
            }
        };
        fetchGenres();
    }, []);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                let searchParam = searchQuery ? `&query=${searchQuery}` : '';
                let endpoint = searchQuery
                    ? `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&page=${page}`
                    : `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&page=${page}${genreParam}`;

                const response = await axios.get(endpoint);

                setMovies(prevMovies => page === 1 ? response.data.results : [...prevMovies, ...response.data.results]);
                setTotalPages(response.data.total_pages);
                setError(null);
            } catch (error) {
                setError('Error fetching movies. Please try again later.');
            }
        };

        fetchMovies();
    }, [selectedGenre, searchQuery, page]);

    const loadMoreMovies = () => {
        if (page < totalPages) {
            setPage(page + 1);
        }
    };

    return (
        <div className="app-container">
            <h1>Movie Recommendations</h1>
            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

            {error ? (
                <p>{error}</p>
            ) : movies.length > 0 ? (
                <>
                    <MovieList movies={movies} />
                    {page < totalPages && (
                        <button onClick={loadMoreMovies}>Load More Movies</button>
                    )}
                </>
            ) : (
                <p>No movies found.</p>
            )}
        </div>
    );
};

export default App;
