import React from 'react';
import './MovieCard.css'; // CSS for styling

const MovieCard = ({ movie, onFavoriteClick, onMovieClick }) => {
    return (
        <div className="movie-card" onClick={() => onMovieClick(movie)}>
            <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
            <h3>{movie.title}</h3>
            <button onClick={(e) => { e.stopPropagation(); onFavoriteClick(movie); }}>Add to Favorites</button>
        </div>
    );
};

export default MovieCard;
