import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext';
import './App.css';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [favorites, setFavorites] = useState([]);
    const [showFavorites, setShowFavorites] = useState(false);
    const [page, setPage] = useState(1);
    const [hasMoreMovies, setHasMoreMovies] = useState(true);

    const selectedGenresList = [
        { id: 28, name: 'Action' },
        { id: 35, name: 'Comedy' },
        { id: 18, name: 'Drama' },
        { id: 27, name: 'Horror' },
        { id: 53, name: 'Thriller' },
        { id: 80, name: 'Crime' },
        { id: 10749, name: 'Romance' },
    ];

    // Fetch movies when the component loads, and when search query, genre, or page changes
    useEffect(() => {
        const fetchMovies = async () => {
            try {
                let response;
                const languageFilter = 'ta';

                if (searchQuery) {
                    // If there's a search query, use the search endpoint
                    response = await axios.get(
                        `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&page=${page}`
                    );
                } else {
                    // Otherwise, use the discover endpoint for genre filtering and pagination
                    const genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                    response = await axios.get(
                        `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}${genreParam}&with_original_language=${languageFilter}&page=${page}`
                    );
                }

                const newMovies = response.data.results.filter(movie => movie.original_language === languageFilter);

                // If we're on page 1, replace the movie list with the new results
                if (page === 1) {
                    setMovies(newMovies);
                } else {
                    // Otherwise, append new results for pagination
                    setMovies((prevMovies) => [...prevMovies, ...newMovies]);
                }

                // If no more movies are available, disable further pagination
                setHasMoreMovies(newMovies.length > 0);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies();
    }, [selectedGenre, searchQuery, page]);

    // Load more movies when the "Load More" button is clicked
    const loadMoreMovies = () => {
        setPage((prevPage) => prevPage + 1);
    };

    // Toggle a movie as favorite
    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id));
        } else {
            setFavorites([...favorites, movie]);
        }
    };

    // Clear search query and reset state
    const clearSearch = () => {
        setSearchQuery('');  // Clear the search query
        setPage(1);  // Reset to the first page
        setMovies([]);  // Clear the movie list
        setHasMoreMovies(true);  // Allow more movies to load
    };

    // When search query or genre changes, reset the page and movies
    useEffect(() => {
        setPage(1);  // Reset page to 1 for new fetch
        setMovies([]);  // Clear the movie list to show new results
        setHasMoreMovies(true);  // Enable pagination for new results
    }, [selectedGenre, searchQuery]);

    return (
        <FavoritesProvider>
            <div className="app-container">
                <h1>Tamil Movie Recommendations</h1>

                <div className="nav-links">
                    <button onClick={() => setShowFavorites(false)}>Home</button>
                    <button onClick={() => setShowFavorites(true)}>Favorites</button>
                </div>

                {!showFavorites ? (
                    <>
                        <div className="search-bar-container">
                            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
                            <button onClick={clearSearch} className="clear-search-button">Clear Search</button>
                        </div>

                        <FilterBar genres={selectedGenresList} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

                        <MovieList movies={movies} toggleFavorite={toggleFavorite} />

                        {hasMoreMovies && (
                            <button onClick={loadMoreMovies} className="load-more-button">Load More Movies</button>
                        )}
                    </>
                ) : (
                    <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                )}
            </div>
        </FavoritesProvider>
    );
};

export default App;
