// src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom'; // Import Router and Link
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext'; // Provider for managing favorites
import './App.css'; // Import the CSS file

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [favorites, setFavorites] = useState([]); // State to store favorites

    // Fetch genres once when the component loads
    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await axios.get(
                    `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=en-US`
                );
                // Filter to include only specific genres like Comedy, Crime, Horror
                setGenres(response.data.genres.filter(genre =>
                    ['Comedy', 'Crime', 'Horror'].includes(genre.name)
                ));
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };
        fetchGenres();
    }, []);

    // Fetch movies whenever selectedGenre, searchQuery, or page changes
    useEffect(() => {
        const fetchMovies = async () => {
            try {
                // Build API query based on selected genre and search query
                let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                let searchParam = searchQuery ? `&query=${searchQuery}` : '';

                const response = await axios.get(
                    `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&with_original_language=ta&page=${page}${genreParam}${searchParam}`
                );

                // If page is 1, replace the movies; otherwise, append to the existing list
                setMovies(prevMovies => page === 1 ? response.data.results : [...prevMovies, ...response.data.results]);

                setTotalPages(response.data.total_pages);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies(); // Call the function to fetch movies based on current state
    }, [selectedGenre, searchQuery, page]); // Re-run whenever these dependencies change

    // Infinite scroll functionality
    useEffect(() => {
        const handleScroll = () => {
            const { scrollTop, scrollHeight, clientHeight } = document.documentElement;
            if (scrollTop + clientHeight >= scrollHeight - 5 && page < totalPages) {
                setPage(prevPage => prevPage + 1);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [page, totalPages]);

    // Function to add or remove a movie from favorites
    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id)); // Remove if already a favorite
        } else {
            setFavorites([...favorites, movie]); // Add to favorites
        }
    };

    // Filtered movies based on search query
    const filteredMovies = movies.filter(movie =>
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <Router>
            <FavoritesProvider>
                <div className="app-container">
                    <nav className="navbar">
                        <h2>Tamil Movie Recommendations</h2>
                        <ul>
                            <li>
                                <Link to="/">Home</Link>
                            </li>
                            <li>
                                <Link to="/favorites">Favorites</Link>
                            </li>
                        </ul>
                    </nav>

                    <Switch>
                        <Route path="/" exact>
                            <h1>Tamil Movie Recommendations</h1>

                            {/* Search bar for searching movies */}
                            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />

                            {/* Filter bar for filtering by genre */}
                            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

                            {/* List of movies with option to add to favorites */}
                            <MovieList movies={filteredMovies} toggleFavorite={toggleFavorite} />
                        </Route>
                        <Route path="/favorites">
                            <h2>Your Favorites</h2>
                            <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                        </Route>
                    </Switch>
                </div>
            </FavoritesProvider>
        </Router>
    );
};

export default App;
