import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext';
import './App.css';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [favorites, setFavorites] = useState([]);
    const [showFavorites, setShowFavorites] = useState(false);
    const [page, setPage] = useState(1);
    const [hasMoreMovies, setHasMoreMovies] = useState(true);

    const selectedGenresList = [
        { id: 28, name: 'Action' },
        { id: 35, name: 'Comedy' },
        { id: 18, name: 'Drama' },
        { id: 27, name: 'Horror' },
        { id: 53, name: 'Thriller' },
        { id: 80, name: 'Crime' },
        { id: 10749, name: 'Romance' },
    ];

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                let response;

                if (searchQuery) {
                    // Use the search endpoint if there's a search query
                    response = await axios.get(
                        `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&page=${page}`
                    );
                } else {
                    // Use the discover endpoint for genre filtering and pagination
                    let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                    response = await axios.get(
                        `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}${genreParam}&page=${page}`
                    );
                }

                const newMovies = response.data.results;

                // Filter out movies that are not in Tamil
                const tamilMovies = newMovies.filter(movie => movie.original_language === 'ta');

                // Append new Tamil movies to the existing list
                setMovies((prevMovies) => [...prevMovies, ...tamilMovies]);

                // Check if there are more movies to load
                if (tamilMovies.length === 0) {
                    setHasMoreMovies(false); // No more movies available
                }
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies();
    }, [selectedGenre, searchQuery, page]);

    const loadMoreMovies = () => {
        setPage((prevPage) => prevPage + 1);
    };

    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id));
        } else {
            setFavorites([...favorites, movie]);
        }
    };

    const clearSearch = () => {
        setSearchQuery('');
        setPage(1);
        setMovies([]);
        setHasMoreMovies(true);
    };

    useEffect(() => {
        setMovies([]);
        setPage(1);
        setHasMoreMovies(true);
    }, [selectedGenre, searchQuery]);

    return (
        <FavoritesProvider>
            <div className="app-container">
                <h1>Tamil Movie Recommendations</h1>

                {/* Navigation Links */}
                <div className="nav-links">
                    <button onClick={() => setShowFavorites(false)}>Home</button>
                    <button onClick={() => setShowFavorites(true)}>Favorites</button>
                </div>

                {/* Conditional Rendering based on showFavorites state */}
                {!showFavorites ? (
                    <>
                        <div className="search-bar-container">
                            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
                            <button onClick={clearSearch} className="clear-search-button">Clear Search</button>
                        </div>

                        <FilterBar genres={selectedGenresList} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

                        <MovieList movies={movies} toggleFavorite={toggleFavorite} />

                        {hasMoreMovies && (
                            <button onClick={loadMoreMovies} className="load-more-button">Load More Movies</button>
                        )}
                    </>
                ) : (
                    <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                )}
            </div>
        </FavoritesProvider>
    );
};

export default App;
