import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext'; // Provider for managing favorites
import './App.css'; // Import the CSS file

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [favorites, setFavorites] = useState([]); // State to store favorites
    const [showFavorites, setShowFavorites] = useState(false); // State to toggle favorites view

    // Fetch genres once when the component loads
    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await axios.get(
                    `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=ta` // Fetching Tamil genres
                );
                // Filter to include only specific genres like Comedy, Crime, Horror
                setGenres(response.data.genres.filter(genre =>
                    ['Comedy', 'Crime', 'Horror'].includes(genre.name)
                ));
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };
        fetchGenres();
    }, []);

    // Fetch movies whenever selectedGenre or page changes
    useEffect(() => {
        const fetchMovies = async () => {
            try {
                // Build API query based on selected genre
                let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';

                const response = await axios.get(
                    `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&with_original_language=ta&page=${page}${genreParam}` // Fetching Tamil movies
                );

                // If page is 1, replace the movies; otherwise, append to the existing list
                setMovies(prevMovies => page === 1 ? response.data.results : [...prevMovies, ...response.data.results]);

                setTotalPages(response.data.total_pages);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies(); // Call the function to fetch movies based on current state
    }, [selectedGenre, page]); // Re-run whenever selectedGenre or page changes

    // Fetch movies based on search query
    useEffect(() => {
        if (searchQuery.trim() === '') {
            // If the search query is empty, reset the page and fetch movies
            setPage(1);
            const fetchMovies = async () => {
                try {
                    const response = await axios.get(
                        `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&with_original_language=ta&page=1` // Fetching Tamil movies
                    );
                    setMovies(response.data.results);
                    setTotalPages(response.data.total_pages);
                } catch (error) {
                    console.error('Error fetching movies:', error);
                }
            };
            fetchMovies();
        } else {
            // If there's a search query, fetch movies based on the query
            const fetchSearchResults = async () => {
                try {
                    const response = await axios.get(
                        `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&language=ta` // Fetching Tamil search results
                    );
                    // Filter out movies that are not Tamil by language
                    setMovies(response.data.results.filter(movie => movie.original_language === 'ta'));
                    setTotalPages(response.data.total_pages);
                } catch (error) {
                    console.error('Error fetching search results:', error);
                }
            };
            fetchSearchResults();
        }
    }, [searchQuery]); // Re-run whenever searchQuery changes

    // Infinite scroll functionality
    useEffect(() => {
        const handleScroll = () => {
            const { scrollTop, scrollHeight, clientHeight } = document.documentElement;
            if (scrollTop + clientHeight >= scrollHeight - 5 && page < totalPages) {
                setPage(prevPage => prevPage + 1);
            }
        };

        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [page, totalPages]);

    // Function to add or remove a movie from favorites
    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id)); // Remove if already a favorite
        } else {
            setFavorites([...favorites, movie]); // Add to favorites
        }
    };

    // Clear search query function
    const clearSearch = () => {
        setSearchQuery('');
    };

    return (
        <FavoritesProvider>
            <div className="app-container">
                <h1>Tamil Movie Recommendations</h1>

                {/* Navigation Links */}
                <div className="nav-links">
                    <button onClick={() => setShowFavorites(false)}>Home</button>
                    <button onClick={() => setShowFavorites(true)}>Favorites</button>
                </div>

                {/* Conditional Rendering based on showFavorites state */}
                {!showFavorites ? (
                    <>
                        {/* Search bar for searching movies */}
                        <div className="search-bar-container">
                            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
                            {/* Clear Search Button */}
                            <button onClick={clearSearch} className="clear-search-button">Clear Search</button>
                        </div>

                        {/* Filter bar for filtering by genre */}
                        <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

                        {/* List of movies with option to add to favorites */}
                        <MovieList movies={movies} toggleFavorite={toggleFavorite} />
                    </>
                ) : (
                    <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                )}
            </div>
        </FavoritesProvider>
    );
};

export default App;
