import React from 'react';

const MovieList = ({ movies }) => {
    if (!movies.length) {
        return <p>No movies found.</p>;
    }

    return (
        <div className="movie-list">
            {movies.map((movie) => (
                <div key={movie.id} className="movie">
                    <img
                        src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} // Ensure poster path is being used correctly
                        alt={movie.title}
                    />
                    <h2>{movie.title}</h2>
                    <p>{movie.overview}</p>
                </div>
            ))}
        </div>
    );
};

export default MovieList;
