import React, { useState } from 'react';

const Login = ({ onLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Here you would typically validate the login credentials
        // For this example, we'll just assume any non-empty fields are valid.
        if (username && password) {
            // Call the onLogin function passed from App.js
            onLogin();
            setError(''); // Clear any previous errors
        } else {
            setError('Please enter both username and password.');
        }
    };

    return (
        <div
            style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                padding: '20px',
                border: '1px solid #ccc',
                borderRadius: '8px',
                backgroundColor: '#f9f9f9',
                width: '300px',
                margin: '0 auto',
            }}
        >
            <h2 style={{ color: '#ff6b00', marginBottom: '20px' }}>Login</h2>
            <form onSubmit={handleSubmit} style={{ width: '100%' }}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    style={{
                        width: '100%',
                        padding: '10px',
                        margin: '5px 0',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                    }}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{
                        width: '100%',
                        padding: '10px',
                        margin: '5px 0',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                    }}
                />
                {error && (
                    <p style={{ color: 'red', fontSize: '0.9rem' }}>{error}</p>
                )}
                <button
                    type="submit"
                    style={{
                        backgroundColor: '#ff6b00',
                        color: '#fff',
                        padding: '10px',
                        borderRadius: '4px',
                        border: 'none',
                        cursor: 'pointer',
                        fontSize: '1em',
                        transition: 'background-color 0.3s',
                        width: '100%',
                    }}
                    onMouseOver={(e) => (e.target.style.backgroundColor = '#e55e00')}
                    onMouseOut={(e) => (e.target.style.backgroundColor = '#ff6b00')}
                >
                    Login
                </button>
            </form>
        </div>
    );
};

export default Login;
