import React, { useContext } from 'react';
import { FavoritesContext } from '../FavoritesContext';
import './MovieCard.css';

const MovieCard = ({ movie }) => {
    const { favorites, addFavorite, removeFavorite } = useContext(FavoritesContext);
    const isFavorite = favorites.some(fav => fav.id === movie.id);

    const handleFavoriteClick = (e) => {
        e.stopPropagation();
        if (isFavorite) {
            removeFavorite(movie.id);
        } else {
            addFavorite(movie);
        }
    };

    return (
        <div className="movie-card" onClick={() => {/* Handle enlarging the movie */}}>
            <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
            <h3>{movie.title}</h3>

            {/* Movie Description */}
            <p className="movie-description">{movie.overview || 'No description available.'}</p>

            {/* Movie Rating */}
            <p className="movie-rating"><strong>Rating:</strong> {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'}/10</p>

            <button onClick={handleFavoriteClick}>
                {isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
            </button>
        </div>
    );
};

export default MovieCard;
