// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import SearchBar from './components/SearchBar';
import FilterBar from './components/FilterBar';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [page, setPage] = useState(1); // Track the current page
    const [totalPages, setTotalPages] = useState(1); // Track the total number of pages

    // Hard-coding the API key (replace with your actual API key)
    const TMDB_API_KEY = '7f99403a9be48af646b906edcdcb5220';

    // Function to fetch movies with pagination
    const fetchMovies = async (pageNum = 1) => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&with_original_language=ta&page=${pageNum}`
            );
            setMovies(prevMovies => [...prevMovies, ...response.data.results]);
            setTotalPages(response.data.total_pages);
        } catch (error) {
            console.error("Error fetching movies:", error);
        }
    };

    // Function to fetch genres
    const fetchGenres = async () => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=ta`
            );
            setGenres(response.data.genres);
        } catch (error) {
            console.error("Error fetching genres:", error);
        }
    };

    // Load more movies when page changes
    const loadMoreMovies = () => {
        if (page < totalPages) {
            setPage(prevPage => prevPage + 1);
        }
    };

    // Fetch movies and genres when the component mounts
    useEffect(() => {
        fetchMovies(page); // Fetch movies for the current page
    }, [page]); // Fetch movies whenever the page changes

    useEffect(() => {
        fetchGenres();
    }, []);

    // Function to handle search query changes
    const handleSearchChange = (query) => {
        console.log("Search Query:", query); // Check if query is being updated
        setSearchQuery(query);
    };

    // Filter movies based on search query and selected genre
    const filteredMovies = movies.filter(movie => {
        // Ensure movie.title is defined before accessing
        const matchesSearch = movie.title 
            ? movie.title.toLowerCase().includes(searchQuery.toLowerCase()) 
            : false;

        const matchesGenre = selectedGenre 
            ? movie.genre_ids && movie.genre_ids.includes(parseInt(selectedGenre)) 
            : true;

        console.log("Movie Title:", movie.title, " | Matches Search:", matchesSearch, " | Matches Genre:", matchesGenre);

        return matchesSearch && matchesGenre; // Movie should match both search and genre filters
    });

    return (
        <div className="App">
            <h1>Tamil Movie Recommendation App</h1>
            <SearchBar searchQuery={searchQuery} setSearchQuery={handleSearchChange} />
            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />
            <MovieList movies={filteredMovies} />
            {page < totalPages && (
                <button onClick={loadMoreMovies}>Load More Movies</button>
            )}
        </div>
    );
};

export default App;
