import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

import MovieList from './components/MovieList';
import FavoritesPage from './components/FavoritesPage';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';

import { FavoritesProvider } from './FavoritesContext';

import './App.css';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    // Fetch genres once when the component loads
    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await axios.get(
                    `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=en-US`
                );
                setGenres(response.data.genres.filter(genre =>
                    ['Comedy', 'Horror', 'Romance', 'Crime', 'Action', 'Drama', 'Thriller'].includes(genre.name)
                ));
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };
        fetchGenres();
    }, []);

    // Fetch all movies based on selected genre and search query
    useEffect(() => {
        const fetchAllMovies = async () => {
            try {
                let allMovies = [];
                let page = 1;
                let totalPages = 1; // To control the loop

                while (page <= totalPages) {
                    let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                    let searchParam = searchQuery ? `&query=${searchQuery}` : '';

                    const response = await axios.get(
                        `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&with_original_language=ta&page=${page}${genreParam}${searchParam}`
                    );

                    allMovies = [...allMovies, ...response.data.results];
                    totalPages = response.data.total_pages; // Update total pages
                    page++; // Increment page
                }

                setMovies(allMovies);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchAllMovies();
    }, [selectedGenre, searchQuery]);

    // Handle search and clear functionality
    const handleSearch = () => {
        setMovies([]); // Reset movies for fresh fetch
    };

    const handleClear = () => {
        setSearchQuery(''); // Clear the search input
        setSelectedGenre(''); // Reset genre filter
        setMovies([]); // Reset movies for fresh fetch
    };

    // Filtered movies based on search query
    const filteredMovies = movies.filter(movie =>
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <FavoritesProvider>
            <Router>
                <div className="app-container">
                    <h1>Tamil Movie Recommendations</h1>
                    <nav>
                        <Link to="/">Home</Link>
                        <Link to="/favorites">Favorites</Link>
                    </nav>
                    <SearchBar 
                        searchQuery={searchQuery} 
                        setSearchQuery={setSearchQuery} 
                        onSearch={handleSearch} 
                        onClear={handleClear} 
                    />
                    <FilterBar 
                        genres={genres} 
                        selectedGenre={selectedGenre} 
                        setSelectedGenre={setSelectedGenre} 
                    />
                    <Routes>
                        <Route path="/" element={<MovieList movies={filteredMovies} />} />
                        <Route path="/favorites" element={<FavoritesPage />} />
                    </Routes>
                </div>
            </Router>
        </FavoritesProvider>
    );
};

export default App;
