import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

import MovieList from './components/MovieList';
import FavoritesPage from './components/FavoritesPage';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';

import { FavoritesProvider } from './FavoritesContext';

import './App.css';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    // Fetch genres once when the component loads
    useEffect(() => {
        const fetchGenres = async () => {
            try {
                const response = await axios.get(
                    `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=en-US`
                );
                setGenres(response.data.genres);
            } catch (error) {
                console.error('Error fetching genres:', error);
            }
        };
        fetchGenres();
    }, []);

    // Fetch all movies without pagination
    useEffect(() => {
        const fetchMovies = async () => {
            try {
                // This endpoint fetches popular movies
                const response = await axios.get(
                    `https://api.themoviedb.org/3/movie/popular?api_key=${TMDB_API_KEY}&language=en-US`
                );

                // Check the response
                console.log('Fetched Movies:', response.data.results);
                setMovies(response.data.results);
            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies();
    }, []);

    // Handle search and clear functionality
    const handleSearch = () => {
        // Reset movies for fresh fetch
        setMovies([]); // This may be unnecessary for a fresh search
    };

    const handleClear = () => {
        setSearchQuery(''); // Clear the search input
        setSelectedGenre(''); // Reset genre filter
    };

    // Filtered movies based on search query
    const filteredMovies = movies.filter(movie =>
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <FavoritesProvider>
            <Router>
                <div className="app-container">
                    <h1>Tamil Movie Recommendations</h1>
                    <nav>
                        <Link to="/">Home</Link>
                        <Link to="/favorites">Favorites</Link>
                    </nav>
                    <SearchBar 
                        searchQuery={searchQuery} 
                        setSearchQuery={setSearchQuery} 
                        onSearch={handleSearch} 
                        onClear={handleClear} 
                    />
                    <FilterBar 
                        genres={genres} 
                        selectedGenre={selectedGenre} 
                        setSelectedGenre={setSelectedGenre} 
                    />
                    <Routes>
                        <Route path="/" element={<MovieList movies={filteredMovies} />} />
                        <Route path="/favorites" element={<FavoritesPage />} />
                    </Routes>
                </div>
            </Router>
        </FavoritesProvider>
    );
};

export default App;
