// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import SearchBar from './components/SearchBar';
import FilterBar from './components/FilterBar';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [page, setPage] = useState(1); // Track the current page
    const [totalPages, setTotalPages] = useState(1); // Track the total number of pages

    // Hard-coding the API key (replace with your actual API key)
    const TMDB_API_KEY = 'bb3b8db0f7a16f48b437de6224ba5881';

    // Function to fetch movies with pagination and search
    const fetchMovies = async (pageNum = 1, query = '') => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${query}&page=${pageNum}&language=ta`
            );
            setMovies(prevMovies => (pageNum === 1 ? response.data.results : [...prevMovies, ...response.data.results]));
            setTotalPages(response.data.total_pages);
        } catch (error) {
            console.error("Error fetching movies:", error);
            alert("Failed to fetch movies. Please try again.");
        }
    };

    // Function to fetch genres
    const fetchGenres = async () => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=ta`
            );
            setGenres(response.data.genres);
        } catch (error) {
            console.error("Error fetching genres:", error);
            alert("Failed to fetch genres. Please try again.");
        }
    };

    // Load more movies when page changes
    const loadMoreMovies = () => {
        if (page < totalPages) {
            setPage(prevPage => prevPage + 1);
        }
    };

    // Fetch movies and genres when the component mounts
    useEffect(() => {
        fetchMovies(page, searchQuery); // Fetch movies for the current page and search query
    }, [page, searchQuery]); // Fetch movies whenever the page or search query changes

    useEffect(() => {
        fetchGenres();
    }, []);

    // Function to handle search query changes
    const handleSearchChange = (query) => {
        setSearchQuery(query);
        setPage(1); // Reset to first page for new search
    };

    // Filter movies based on selected genre
    const filteredMovies = movies.filter(movie => {
        const matchesGenre = selectedGenre 
            ? movie.genre_ids && movie.genre_ids.includes(parseInt(selectedGenre)) 
            : true;

        return matchesGenre; // Only filter by genre; search is handled separately
    });

    return (
        <div className="App">
            <h1>Tamil Movie Recommendation App</h1>
            <SearchBar searchQuery={searchQuery} setSearchQuery={handleSearchChange} />
            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />
            <MovieList movies={filteredMovies} />
            {page < totalPages && (
                <button onClick={loadMoreMovies}>Load More Movies</button>
            )}
        </div>
    );
};

export default App;
