import React, { useState } from 'react';
import axios from 'axios';
import './SignUp.css';

const SignUp = ({ onSignUp, onToggleSignUp }) => {
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [mobile, setMobile] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    const handleSignUp = async (e) => {
        e.preventDefault();
        setError('');

        if (password !== confirmPassword) {
            setError("Passwords don't match!");
            return;
        }

        try {
            const response = await axios.post('http://localhost:5000/api/auth/register', {
                email,
                username,
                mobile,
                password,
            });

            if (response.status === 201) {
                onSignUp();
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Sign-up failed');
            console.error(err);
        }
    };

    const handleBack = () => {
        onToggleSignUp(); // Navigate back to Login page
    };

    return (
        <div
            className="container-fluid d-flex justify-content-center align-items-center vh-100"
        >
            <div
                className="card p-3 shadow-lg"
                style={{
                    maxWidth: '300px',
                    width: '100%',
                    padding: '15px',
                    background: 'rgba(255, 255, 255, 0.7)',
                    backdropFilter: 'blur(8px)',
                    borderRadius: '10px',
                    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.1)',
                    color: '#333',
                    background: 'linear-gradient(360deg, rgba(196,215,115,1) 0%, rgba(254,149,93,1) 100%)'
                }}
            >
                <div className='row'>
                    <div className='col-1' onClick={handleBack}>
                        <i className="bi bi-arrow-left-circle-fill"></i>
                    </div>
                    <div className='col'>
                        <h4 className="text-center text-dark mb-3" style={{ fontSize: '1.2rem' }}>
                            Create an account
                        </h4>
                    </div>
                </div>

                {error && <div className="alert alert-danger text-center" style={{ fontSize: '0.9rem' }}>{error}</div>}

                <form onSubmit={handleSignUp}>
                    {/* Form fields for email, username, mobile, password, confirm password */}
                    <button type="submit" className="btn btn-outline-primary w-100 mt-2" style={{ padding: '0.5rem', fontSize: '0.9rem' }}>
                        Sign Up
                    </button>
                </form>
            </div>
        </div>
    );
};

export default SignUp;
