import React from 'react';
import './MovieCard.css'; // Ensure this file exists for styling

const MovieCard = ({ movie }) => {
    return (
        <div className="movie-card">
            {/* Movie Poster */}
            {movie.poster_path ? (
                <img src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`} alt={movie.title} className="movie-poster" />
            ) : (
                <div className="no-poster">No Poster Available</div>
            )}

            {/* Movie Title */}
            <h3 className="movie-title">{movie.title}</h3>

            {/* Movie Description */}
            <p className="movie-description">{movie.overview || 'No description available.'}</p>

            {/* Movie Rating */}
            <p className="movie-rating"><strong>Rating:</strong> {movie.vote_average ? movie.vote_average.toFixed(1) : 'N/A'}/10</p>
        </div>
    );
};

export default MovieCard;
