// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import SearchBar from './components/SearchBar';
import FilterBar from './components/FilterBar';

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [page, setPage] = useState(1); // Track the current page
    const [totalPages, setTotalPages] = useState(1); // Track the total number of pages

    // Hard-coding the API key (replace with your actual API key)
    const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a';

    // Function to fetch movies with pagination
    const fetchMovies = async (pageNum = 1) => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}&language=te&page=${pageNum}`
            );
            // Update the movies list by concatenating new results
            setMovies(prevMovies => [...prevMovies, ...response.data.results]);
            setTotalPages(response.data.total_pages); // Set the total pages from the API response
        } catch (error) {
            console.error("Error fetching movies:", error);
        }
    };

    // Function to fetch genres
    const fetchGenres = async () => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/genre/movie/list?api_key=${TMDB_API_KEY}&language=en-US`
            );
            setGenres(response.data.genres);
        } catch (error) {
            console.error("Error fetching genres:", error);
        }
    };

    // Load more movies when page changes
    const loadMoreMovies = () => {
        if (page < totalPages) {
            setPage(prevPage => prevPage + 1);
        }
    };

    // Fetch movies and genres when the component mounts
    useEffect(() => {
        fetchMovies(page); // Fetch movies for the current page
    }, [page]); // Fetch movies whenever the page changes

    useEffect(() => {
        fetchGenres();
    }, []);

    // Function to handle search query changes
    const handleSearchChange = (query) => {
        setSearchQuery(query);
    };

    // Filter movies based on search query and selected genre
    const filteredMovies = movies.filter(movie => {
        const matchesSearch = movie.title.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesGenre = selectedGenre ? movie.genre_ids.includes(parseInt(selectedGenre)) : true;
        return matchesSearch && matchesGenre;
    });

    return (
        <div className="App">
            <h1>Tamil Movie Recommendation App</h1>
            <SearchBar searchQuery={searchQuery} setSearchQuery={handleSearchChange} />
            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />
            <MovieList movies={filteredMovies} />
            {page < totalPages && (
                <button onClick={loadMoreMovies}>Load More Movies</button>
            )}
        </div>
    );
};

export default App;
