import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext';
import Login from './components/Login';
import SignUp from './components/Signup';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [favorites, setFavorites] = useState([]);
  const [showFavorites, setShowFavorites] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [isUserSignedUp, setIsUserSignedUp] = useState(false);

  const selectedGenresList = [
    { id: 28, name: 'Action' },
    { id: 35, name: 'Comedy' },
    { id: 18, name: 'Drama' },
    { id: 27, name: 'Horror' },
    { id: 53, name: 'Thriller' },
    { id: 80, name: 'Crime' },
    { id: 10749, name: 'Romance' },
  ];

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        let movieResults = [];
        let totalPages = 20;

        for (let currentPage = 1; currentPage <= totalPages; currentPage++) {
          let response;

          if (searchQuery) {
            response = await axios.get(
              `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&with_original_language=ta&page=${currentPage}`
            );
          } else {
            let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
            response = await axios.get(
              `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}${genreParam}&with_original_language=ta&page=${currentPage}`
            );
          }

          const fetchedMovies = response.data.results;
          const tamilMovies = fetchedMovies.filter(movie => movie.original_language === 'ta');
          movieResults = [...movieResults, ...tamilMovies];

          if (response.data.total_pages <= currentPage) {
            break;
          }
        }

        setMovies(movieResults);

      } catch (error) {
        console.error('Error fetching movies:', error);
      }
    };

    fetchMovies();
  }, [selectedGenre, searchQuery]);

  const toggleFavorite = (movie) => {
    if (favorites.some(favorite => favorite.id === movie.id)) {
      setFavorites(favorites.filter(fav => fav.id !== movie.id));
    } else {
      setFavorites([...favorites, movie]);
    }
  };

  const clearSearch = () => {
    setSearchQuery('');
    setMovies([]);
  };

  return (
    <FavoritesProvider>
      {!isLoggedIn ? (
        !isUserSignedUp ? (
          <SignUp
            onSignUp={() => {
              setIsUserSignedUp(true);
              setIsLoggedIn(true);
            }}
          />
        ) : (
          <Login onLogin={() => setIsLoggedIn(true)} />
        )
      ) : (
        <div
          style={{
            backgroundColor: '#f5f5f5',
            color: '#333',
            padding: '20px',
            minHeight: '100vh',
          }}
        >
          <h1 style={{ fontFamily: 'Poppins, sans-serif', color: '#5e60ce' }}>
            Tamil Movie Recommendations
          </h1>
          <button
            style={{
              backgroundColor: '#64dfdf',
              color: '#fff',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginBottom: '20px',
            }}
            onClick={() => setIsLoggedIn(false)}
          >
            Logout
          </button>

          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              marginBottom: '20px',
            }}
          >
            <button
              style={{
                backgroundColor: '#b5179e',
                color: '#fff',
                padding: '10px 15px',
                borderRadius: '5px',
                border: 'none',
                cursor: 'pointer',
              }}
              onClick={() => setShowFavorites(false)}
            >
              Home
            </button>
            <button
              style={{
                backgroundColor: '#3a86ff',
                color: '#fff',
                padding: '10px 15px',
                borderRadius: '5px',
                border: 'none',
                cursor: 'pointer',
              }}
              onClick={() => setShowFavorites(true)}
            >
              Favorites
            </button>
          </div>

          {!showFavorites ? (
            <>
              <div
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  marginBottom: '20px',
                }}
              >
                <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
                <button
                  onClick={clearSearch}
                  style={{
                    backgroundColor: '#ff006e',
                    color: '#fff',
                    padding: '8px 15px',
                    marginLeft: '10px',
                    border: 'none',
                    borderRadius: '5px',
                    cursor: 'pointer',
                  }}
                >
                  Clear Search
                </button>
              </div>

              <FilterBar genres={selectedGenresList} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

              <MovieList movies={movies} toggleFavorite={toggleFavorite} />
            </>
          ) : (
            <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
          )}
        </div>
      )}
      <button
        onClick={() => setIsSignUp(!isSignUp)}
        style={{
          backgroundColor: '#8338ec',
          color: '#fff',
          padding: '10px 20px',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          marginTop: '20px',
        }}
      >
        {isSignUp ? 'Go to Login' : 'Go to Sign Up'}
      </button>
    </FavoritesProvider>
  );
};

export default App;
