import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList'; // Ensure you have this component
import SearchBar from './components/SearchBar'; // Ensure you have this component
import FilterBar from './components/FilterBar'; // Ensure you have this component

const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [genres, setGenres] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');

    // Function to fetch Tamil movies
    const fetchMovies = async () => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_TMDB_API_KEY}&language=ta`
            );
            setMovies(response.data.results);
        } catch (error) {
            console.error("Error fetching Tamil movies:", error);
        }
    };

    // Function to fetch genres
    const fetchGenres = async () => {
        try {
            const response = await axios.get(
                `https://api.themoviedb.org/3/genre/movie/list?api_key=${process.env.REACT_APP_TMDB_API_KEY}&language=en-US`
            );
            setGenres(response.data.genres);
        } catch (error) {
            console.error("Error fetching genres:", error);
        }
    };

    // useEffect to fetch movies and genres on component mount
    useEffect(() => {
        fetchMovies();  // Call to fetch Tamil movies
        fetchGenres();  // Call to fetch movie genres
    }, []); // Empty dependency array to run only on mount

    // Filter movies based on search query and selected genre
    const filteredMovies = movies.filter(movie => {
        const matchesSearch = movie.title.toLowerCase().includes(searchQuery.toLowerCase());
        const matchesGenre = selectedGenre ? movie.genre_ids.includes(parseInt(selectedGenre)) : true;
        return matchesSearch && matchesGenre;
    });

    return (
        <div className="App">
            <h1>Tamil Movie Recommendation App</h1>
            <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
            <FilterBar genres={genres} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />
            <MovieList movies={filteredMovies} />
        </div>
    );
};

export default App;
