import React, { useContext } from 'react';
import { FavoritesContext } from '../FavoritesContext';
import MovieCard from './MovieCard';
import './FavoritesPage.css'; // Optional CSS for styling

const FavoritesPage = () => {
    const { favorites, removeFavorite } = useContext(FavoritesContext); // Ensure removeFavorite is available

    return (
        <div className="favorites-page">
            <h1>Your Favorites</h1>
            {favorites.length === 0 ? (
                <p>No favorites added yet.</p>
            ) : (
                favorites.map(movie => (
                    <div key={movie.id}>
                        <MovieCard movie={movie} />
                        <button onClick={() => removeFavorite(movie.id)}>Remove from Favorites</button>
                    </div>
                ))
            )}
        </div>
    );
};

export default FavoritesPage;
