import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ onLogin, onToggleSignUp }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
    
        try {
            const response = await axios.post('http://localhost:5000/api/auth/login', {
                email,
                password,
            });
    
            if (response.status === 200) {
                localStorage.setItem('token', response.data.token); // Store token
                localStorage.setItem('userId', response.data.userId); // Store userId (if provided)
                onLogin(); // Call onLogin callback if successful
            }
        } catch (err) {
            setError('Invalid credentials or server error');
            console.error(err);
        }
    };
    

    return (
        <div
            className="container d-flex justify-content-center align-items-center vh-100"
            style={{
                background: 'linear-gradient(135deg, rgba(255, 107, 0, 0.8), rgba(161, 196, 253, 0.8))',
            }}
        >
            <div className="card p-4 shadow-lg login-card">
                <h3 className="text-center text-dark mb-4">Login</h3>

                {error && <div className="alert alert-danger text-center">{error}</div>}

                <form onSubmit={handleLogin}>
                    <div className="mb-3">
                        <label className="form-label">Email</label>
                        <input
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="form-control"
                            placeholder="Enter your email"
                            required
                        />
                    </div>

                    <div className="mb-3">
                        <label className="form-label">Password</label>
                        <input
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="form-control"
                            placeholder="Enter your password"
                            required
                        />
                    </div>

                    <button type="submit" className="btn btn-primary w-100 mt-3">
                        Login
                    </button>

                    <p className="text-center mt-3">
                        Don't have an account? <button className="btn-link" onClick={onToggleSignUp}>Sign Up</button>
                    </p>
                </form>
            </div>
        </div>
    );
};

export default Login;
