import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './components/MovieList';
import FilterBar from './components/FilterBar';
import SearchBar from './components/SearchBar';
import FavoritesPage from './components/FavoritesPage';
import { FavoritesProvider } from './FavoritesContext';
import Login from './components/Login';
//import SignUp from './components/Signup';
import './App.css';

const TMDB_API_KEY = '82bf8e7015e539b6b3839975fa59392a'; // Replace with your TMDB API Key

const App = () => {
    const [movies, setMovies] = useState([]);
    const [selectedGenre, setSelectedGenre] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [favorites, setFavorites] = useState([]);
    const [showFavorites, setShowFavorites] = useState(false);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isSignUp, setIsSignUp] = useState(false);
    const [isUserSignedUp, setIsUserSignedUp] = useState(false); // New state to track if user is signed up

    const selectedGenresList = [
        { id: 28, name: 'Action' },
        { id: 35, name: 'Comedy' },
        { id: 18, name: 'Drama' },
        { id: 27, name: 'Horror' },
        { id: 53, name: 'Thriller' },
        { id: 80, name: 'Crime' },
        { id: 10749, name: 'Romance' },
    ];

    // Fetch all movies (with search or genre filtering)
    useEffect(() => {
        const fetchMovies = async () => {
            try {
                let movieResults = [];
                let totalPages = 20;  // Fetch up to 20 pages (400 movies)

                for (let currentPage = 1; currentPage <= totalPages; currentPage++) {
                    let response;

                    if (searchQuery) {
                        // Fetch movies based on search query
                        response = await axios.get(
                            `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${searchQuery}&with_original_language=ta&page=${currentPage}`
                        );
                    } else {
                        // Fetch movies based on selected genre (if any)
                        let genreParam = selectedGenre ? `&with_genres=${selectedGenre}` : '';
                        response = await axios.get(
                            `https://api.themoviedb.org/3/discover/movie?api_key=${TMDB_API_KEY}${genreParam}&with_original_language=ta&page=${currentPage}`
                        );
                    }

                    // If response is successful
                    const fetchedMovies = response.data.results;
                    const tamilMovies = fetchedMovies.filter(movie => movie.original_language === 'ta');
                    movieResults = [...movieResults, ...tamilMovies];  // Append new movies to results

                    // Break if no more movies to fetch
                    if (response.data.total_pages <= currentPage) {
                        break;
                    }
                }

                // Set movies once all pages are fetched
                setMovies(movieResults);

            } catch (error) {
                console.error('Error fetching movies:', error);
            }
        };

        fetchMovies();
    }, [selectedGenre, searchQuery]);

    // Function to toggle a movie as a favorite
    const toggleFavorite = (movie) => {
        if (favorites.some(favorite => favorite.id === movie.id)) {
            setFavorites(favorites.filter(fav => fav.id !== movie.id));
        } else {
            setFavorites([...favorites, movie]);
        }
    };

    // Clear search query and reset state
    const clearSearch = () => {
        setSearchQuery('');  // Clear the search query
        setMovies([]);  // Clear the movie list
    };

    // Render app conditionally based on login state and if the user has signed up
    return (
        <FavoritesProvider>
            {!isLoggedIn ? (
                !isUserSignedUp ? (
                    <SignUp
                        onSignUp={() => {
                            setIsUserSignedUp(true);
                            setIsLoggedIn(true);
                        }}
                    />
                ) : (
                    <Login onLogin={() => setIsLoggedIn(true)} />
                )
            ) : (
                <div className="app-container">
                    <h1>Tamil Movie Recommendations</h1>
                    <button onClick={() => setIsLoggedIn(false)}>Logout</button>

                    <div className="nav-links">
                        <button onClick={() => setShowFavorites(false)}>Home</button>
                        <button onClick={() => setShowFavorites(true)}>Favorites</button>
                    </div>

                    {!showFavorites ? (
                        <>
                            <div className="search-bar-container">
                                <SearchBar searchQuery={searchQuery} setSearchQuery={setSearchQuery} />
                                <button onClick={clearSearch} className="clear-search-button">Clear Search</button>
                            </div>

                            <FilterBar genres={selectedGenresList} selectedGenre={selectedGenre} setSelectedGenre={setSelectedGenre} />

                            <MovieList movies={movies} toggleFavorite={toggleFavorite} />
                        </>
                    ) : (
                        <FavoritesPage favorites={favorites} toggleFavorite={toggleFavorite} />
                    )}
                </div>
            )}
            <button onClick={() => setIsSignUp(!isSignUp)}>
                {isSignUp ? 'Go to Login' : 'Go to Sign Up'}
            </button>
        </FavoritesProvider>
    );
};

export default App;
